  }

  /**
   * Return the current version of Canvasimo (and log to console if logInfo parameter is true)
   * @alias version
   */
  public getVersion = (logInfo?: BooleanFalsy): string => {
    if (logInfo) {
      logger.info(`Using version ${VERSION}`);
    }

    return VERSION;
  }
  public version = (logInfo?: BooleanFalsy): string => this.getVersion(logInfo);

  // Set and get context properties
  private setCanvasProperty = (attribute: string, value: any): Canvasimo => {
    (this.ctx as any)[attribute] = value;
    return this;
  }
  private getCanvasProperty = (attribute: string) => (this.ctx as any)[attribute];
  private drawTextWithLineBreaks = (
    method: this['strokeText'] | this['fillText'],
    text: string,
    x: number,
    y: number,
    lineHeight?: number,
    color?: string
  ): Canvasimo => {
    const height = typeof lineHeight === 'number' ? lineHeight : 1;
    const definedFontSize = this.getFontSize();
    const fontSize = typeof definedFontSize === 'number' ? definedFontSize : 10;

    const lines = text.split('
');

    lines.forEach((line, index) => {
      const offset = fontSize * height;
      method(line, x, y + offset * index, undefined, color);
    });

    return this;
  }
  private wrapBreakAll = (text: string, maxWidth: number, lines: string[] = ['']): string[] => {
    const letters = text.split('');
    let lineIndex = lines.length - 1;

    letters.forEach((letter) => {
      const line = lines[lineIndex];
      const { width: newLineWidth } = this.getTextSize(line + letter);

      if (newLineWidth < maxWidth || line.length === 0) {
        lines[lineIndex] += letter;
      } else {
        lines.push('');
        lineIndex = lines.length - 1;

        if (!MATCHES_ALL_WHITESPACE.test(letter)) {
          lines[lineIndex] += letter;
        }
      }
    });

    return lines;
  }
  private wrapBreakWord = (text: string, maxWidth: number, lines: string[] = ['']): string[] => {
    const words = text.split(MATCHES_WORD_BREAKS);
    let lineIndex = 0;

    words.forEach((word) => {
      const line = lines[lineIndex];
      const { width: newLineWidth } = this.getTextSize(line + word);

      if (newLineWidth < maxWidth) {
        lines[lineIndex] += word;
      } else {
        if (!MATCHES_ALL_WHITESPACE.test(word)) {
          if (line.length > 0) {
            lines.push('');
          }
          lines = this.wrapBreakAll(word, maxWidth, lines);
        }

        lineIndex = lines.length - 1;
      }
    });

    return lines;
  }
  private wrapNormal = (text: string, maxWidth: number, lines: string[] = ['']): string[] => {
    const words = text.split(MATCHES_WORD_BREAKS);
    let lineIndex = 0;

    words.forEach((word) => {
      const line = lines[lineIndex];
      const { width: newLineWidth } = this.getTextSize(line + word);

      if (newLineWidth < maxWidth || line.length === 0) {
        lines[lineIndex] += word;
      } else {
        if (!MATCHES_ALL_WHITESPACE.test(word)) {
          lines.push(word);
        } else {
          lines.push('');
        }

        lineIndex = lines.length - 1;
      }
    });

    return lines;
  }
  private textMultiline = (
    method: this['strokeText'] | this['fillText'],
    text: string,
    x: number,
    y: number,
    maxWidth?: MaxWidth,
    wordBreak?: WordBreak,
    lineHeight?: number,
    color?: string
  ): Canvasimo => {
    if (typeof maxWidth === 'undefined' || maxWidth === null) {
      return this.drawTextWithLineBreaks(method, text, x, y, lineHeight, color);
    } else if (wordBreak === 'break-all') {
      const lines = text.split('
').map((subText) => this.wrapBreakAll(subText, maxWidth).join('
'));
      return this.drawTextWithLineBreaks(method, lines.join('
'), x, y, lineHeight, color);
    } else if (wordBreak === 'break-word') {
      const lines = text.split('
').map((subText) => this.wrapBreakWord(subText, maxWidth).join('
'));
      return this.drawTextWithLineBreaks(method, lines.join('
'), x, y, lineHeight, color);
    } else {
      const lines = text.split('
').map((subText) => this.wrapNormal(subText, maxWidth).join('
'));
      return this.drawTextWithLineBreaks(method, lines.join('
'), x, y, lineHeight, color);
    }
  }
}

export default Canvasimo;
const consoleExists = Boolean(window.console);

const logger = {
  info: (message: string) => {
    if (consoleExists) {
      window.console.info(`Canvasimo: ${message}`);
    }
  },
  warn: (message: string) => {
    if (consoleExists) {
      window.console.warn(`Canvasimo: ${message}`);
    }
  },
};

export default logger;
import {
  DEFAULT_FONT,
  INCORRECT_POINT_FORMAT,
  MATCHES_FONT_SIZE,
  MATCHES_FONT_STYLE,
  MATCHES_FONT_VARIANT,
  MATCHES_FONT_WEIGHT,
  MATCHES_NORMAL,
  MATCHES_SPECIAL_FILL,
  MATCHES_SPECIAL_FONT,
  MATCHES_WHITESPACE,
} from './constants';
import logger from './logger';
import {
  FillRule,
  Points,
} from './types';

export const isPoint = (point?: {x: number, y: number} | [number, number] | number):
  point is {x: number, y: number} => {
  return typeof point === 'object' && 'x' in point && 'y' in point;
};

export const isTuplePoint = (point?: {x: number, y: number} | [number, number] | number):
  point is [number, number] => {
  return typeof point === 'object' && Array.isArray(point) && point.length === 2;
};

let warnedAboutLineHeight = false;

export const getFontParts = (input: string | undefined, density: number, getter: boolean) => {
  if (!input) {
    return DEFAULT_FONT;
  }

  const font = input.trim();

  if (MATCHES_SPECIAL_FONT.test(font)) {
    return [font];
  }

  const matchFontSize = MATCHES_FONT_SIZE.exec(font);

  if (!matchFontSize) {
    return DEFAULT_FONT;
  }

  const fontString = matchFontSize[0].trim();
  const leadingSpaces = matchFontSize[1].length;
  const size = matchFontSize[2];
  const unit = matchFontSize[3];
  const lineHeight = matchFontSize[4];

  if (lineHeight && !warnedAboutLineHeight) {
    logger.warn(
      `Attempted to set the font line height with "${fontString}", ` +
      'but this is not supported by canvas. ' +
      'Use the Canvasimo TextMultiline methods with the lineHeight parameter instead.'
    );
    warnedAboutLineHeight = true;
  }

  const fontSize = (unit !== '%' ? (getter ? parseFloat(size) / density : parseFloat(size) * density) : size) + unit;

  const parts = font.substring(matchFontSize.index + leadingSpaces).split(MATCHES_WHITESPACE);
  const fontFamily = parts[parts.length - 1];

  const optional = font.substring(0, matchFontSize.index);
  const optionalParts = optional ? optional.split(MATCHES_WHITESPACE) : null;

  let fontStyle;
  let fontVariant;
  let fontWeight;

  if (optionalParts) {
    while (optionalParts.length) {
      if (MATCHES_FONT_STYLE.test(optionalParts[0])) {
        fontStyle = optionalParts.splice(0, 1)[0];
      } else if (MATCHES_FONT_VARIANT.test(optionalParts[0])) {
        fontVariant = optionalParts.splice(0, 1)[0];
      } else if (MATCHES_FONT_WEIGHT.test(optionalParts[0])) {
        fontWeight = optionalParts.splice(0, 1)[0];
      } else if (MATCHES_NORMAL.test(optionalParts[0])) {
        optionalParts.splice(0, 1);
      } else {
        return DEFAULT_FONT;
      }
    }
  }

  return [
    fontStyle || DEFAULT_FONT[0],
    fontVariant || DEFAULT_FONT[1],
    fontWeight || DEFAULT_FONT[2],
    fontSize,
    fontFamily,
  ];
};

export const formatFont = (input: string, density: number, getter: boolean): string =>
  getFontParts(input, density, getter).join(' ');

export const forPoints = (points: Points, callback: (x: number, y: number, index: number) => any): void => {
  if (!Array.isArray(points) || (typeof points[0] === 'number' && (points.length % 2) !== 0)) {
    throw new Error(INCORRECT_POINT_FORMAT);
  }

  if (!points.length || points.length === 1 || (typeof points[0] === 'number' && points.length < 4)) {
    return;
  }

  const firstPoint = points[0];
  const secondPoint = points[1];

  if (isPoint(firstPoint)) {
    (points as Array<{x: number, y: number}>).forEach((point, index) => {
      if (!isPoint(point)) {
        throw new Error(`Expected point with format {x, y} but got ${point}`);
      }

      callback(point.x, point.y, index);
    });
  } else if (isTuplePoint(firstPoint)) {
    (points as Array<[number, number]>).forEach((point, index) => {
      if (!isTuplePoint(point)) {
        throw new Error(`Expected point with format [x, y] but got ${point}`);
      }

      callback(point[0], point[1], index);
    });
  } else if (typeof secondPoint === 'number') {
    // tslint:disable-next-line:prefer-for-of
    for (let i = 0; i < points.length; i += 2) {
      const pointX = points[i];
      const pointY = points[i + 1];

      if (typeof pointX !== 'number' || typeof pointY !== 'number') {
        throw new Error(`Expected points to be an array of numbers but got ${pointX}, ${pointY}`);
      }

      callback(pointX, pointY, i / 2);
    }
  } else {
    throw new Error(INCORRECT_POINT_FORMAT);
  }
};

export const isFillRule = (value: any): value is FillRule => typeof value === 'string' &&
  MATCHES_SPECIAL_FILL.test(value);
